select measure_id, measure_name, round(sum(score),2) as agg_score, round(avg(score),2) as avg_score, round(variance(score),2) as var_score from (select m.measure_id, m.measure_name, ec.score from effective_care_c ec join measures_c m on ec.measure_id = m.measure_id union all select m.measure_id, m.measure_name, r.score from readmissions_c r join measures_c m on r.measure_id = m.measure_id) h group by measure_id, measure_name order by var_score desc limit 10;
